function twitterCallback2(a){var b=[];for(var c=0;c<a.length;c++){var d=a[c].user.screen_name,e=a[c].text.replace(/((https?|s?ftp|ssh)\:\/\/[^"\s\<\>]*[^.,;'">\:\s\<\>\)\]\!])/g,function(a){return'<a href="'+a+'">'+a+"</a>"}).replace(/\B@([_a-z0-9]+)/ig,function(a){return a.charAt(0)+'<a href="http://twitter.com/'+a.substring(1)+'">'+a.substring(1)+"</a>"});b.push("<li><span>"+e+'</span> <a style="font-size:85%" href="http://twitter.com/'+d+"/statuses/"+a[c].id_str+'">'+relative_time(a[c].created_at)+"</a></li>")}document.getElementById("twitter_update_list").innerHTML=b.join("")}function relative_time(a){var b=a.split(" ");a=b[1]+" "+b[2]+", "+b[5]+" "+b[3];var c=Date.parse(a),d=arguments.length>1?arguments[1]:new Date,e=parseInt((d.getTime()-c)/1e3);return e+=d.getTimezoneOffset()*60,e<60?"less than a minute ago":e<120?"about a minute ago":e<3600?parseInt(e/60).toString()+" minutes ago":e<7200?"about an hour ago":e<86400?"about "+parseInt(e/3600).toString()+" hours ago":e<172800?"1 day ago":parseInt(e/86400).toString()+" days ago"};